    selectedArtist,
    selectedGenre,
    selectedReleaseDate,
    selectedLabel,

    handleSelectedArtist,
    handleSelectedGenre,
    handleSelectedReleaseDate,
    handleSelectedLabel,

    filteredRecs
}) => {
    return (
        <div>
          <label>Artist:</label>
          <select value={selectedArtist} onChange={handleArtistChange}>
            <option value="">All Artists</option>
            {/* Add options dynamically based on your data */}

          </select>
    
          <label>Genre:</label>
          <select value={selectedGenre} onChange={handleGenreChange}>
            <option value="">All Genres</option>
            {/* Add options dynamically based on your data */}
          </select>
    
          <button onClick={filterSongs}>Apply Filters</button>
        </div>
      );
}
