    selectedArtist,
    selectedGenre,
    selectedReleaseDate,
    selectedLabel,

    handleArtistChange,
    handleGenreChange,
    handleReleaseDateChange,
    handleLabelChange,

    filterRecords,
    

}) => {
    // data for select list
