    const handleFilterClick = () => {
        filterRecords();
        console.log('hello');
    }

    return (
        <div>
          <label>Artist:</label>
          <select value={selectedArtist} onChange={handleArtistChange}>
            <option value="">All</option>
            {records.map(rec => (
                <option key={rec.record_id}>{rec.artist}</option>
            ))}

          </select>
    
          <label>Genre:</label>
          <select value={selectedGenre} onChange={handleGenreChange}>
            <option value="">All</option>
            {records.map(rec => (
                <option key={rec.record_id}>{rec.genre}</option>
            ))}
          </select>
          <label>Label:</label>
          <select value={selectedLabel} onChange={handleLabelChange}>
            <option value="">All</option>
            {records.map(rec => (
                <option key={rec.record_id}>{rec.label}</option>
            ))}
          </select>
          <label>Release Date:</label>
          <select value={selectedReleaseDate} onChange={handleReleaseDateChange}>
            <option value="">All</option>
            {records.map(rec => (
                <option key={rec.record_id}>{rec.release_date}</option>
            ))}
          </select>
    
          <button onClick={handleFilterClick}>Apply Filters</button>
        </div>
      );
}; export default Filter;
