 * genre
 * release_date
 * label
 * price
 * tracklist
 * cover_image
 */

const InventoryList = () => {
  const [data, setData] = useState([]);
  const [filteredData, setFilteredData] = useState([]);

  useEffect(() => {
    // Fetch and parse JSON data when the component mounts
    fetch("/data/mock_vinyls.json") // Note: This path is relative to the public directory in CodeSandbox
      .then((response) => response.json())
      .then((jsonData) => {
        console.log(jsonData);
        setData(jsonData);
      })
      .catch((error) => {
        console.error("Error fetching data:", error);
      });
  }, []); //empty dependency array ensures that this effect runs once when the component mounts.

  // Define filter criteria
  const FilterData = () => {
    const genre = data.filter(i => i.genre === 'pop');
    setFilteredData(genre);
  };
  // call filterData function when componet is renered.
  useEffect(() => {
    FilterData();
  },[data])

