import { useEffect } from 'react'; 


/**
 * artist
 * album
 * genre
 * release_date
 * label
 * price
 * tracklist
 * cover_image
 */
const InventoryList = () => {

  const [records, setRecords] = useState([])

  // Read JSON data
  useEffect(() => {
    fetch('/data/mock_vinyls.json')
    .then(res => res.json())
    .then(data => {
        setRecords(data);
        setCategories([...new Set(data.map(p => p.artist))])
    })
    .catch(err => console.error(err));
}, []);

  return (
    <div className="container">
      <div className="row">
        <div className="col">
          
          {records.map(p => (
            <div style={{display: "flex"}} className="row">
              <img
                src={p.cover_image} alt="album artwork/covers"
                style={{
                  height: 300,
                  width: 300,
                  marginTop: 20
                }}
              />
              <span class="row">
                <b className="text-info">Artist:</b> 
                <p className="vinyl-info">{p.artist}</p>
              </span>
              <span class="row">
                <b className="text-info">Genre:</b> 
                <p className="vinyl-info">{p.genre}</p>
              </span>
              <span class="row">
                <b className="text-info">Release Date:</b> 
                <p className="vinyl-info">{p.release_date}</p>
              </span>
              <span class="row">
                <b className="text-info">Label:</b> 
                <p className="vinyl-info">{p.label}</p>
              </span>
              <span class="row">
                <b className="text-info ">Price:</b> 
                <p className="vinyl-info" style={{color: "var(--money-green)"}}>$ {p.price} </p>
              </span>
              <span class="row">
                <b className="text-info">Tracklist: </b>
                <p className="vinyl-info">{p.tracklist}</p>
              </span>
              <span className="hr"></span>
            </div>
            
          )) }
          </div>          
      </div>    
    </div>    
      
  );
}; export default InventoryList;
