const Filter = ({ categories, onSelectArtist, onSelectAlbum, onSelectGenre, onSelectReleaseDate, onSelectLabel}) => {
    return (
        <>
          <div> 
                <h3>Filters</h3>
                <label>Artist</label><br/>
                <select onChange={(e) => onSelectArtist(e.target.value)}>
                    <option value="">All</option>
                    {categories.map(category => (
                        <option key={category} value={category}>{category}</option>
                    ))};
                </select>
                <label>Album</label><br/>
                <select onChange={(e) => onSelectAlbum(e.target.value)}>
                    <option value="">All</option>
                    {categories.map(category => (
                        <option key={category} value={category}>{category}</option>
                    ))};
                </select>
                <label>Genre</label><br/>
                <select onChange={(e) => onSelectGenre(e.target.value)}>
                    <option value="">All</option>
                    {categories.map(category => (
                        <option key={category} value={category}>{category}</option>
                    ))};
                </select>
                <label>Release Date</label><br/>
                <select onChange={(e) => onSelectReleaseDate(e.target.value)}>
                    <option value="">All</option>
                    {categories.map(category => (
                        <option key={category} value={category}>{category}</option>
                    ))};
                </select>
                <label>Label</label><br/>
                <select onChange={(e) => onSelectLabel(e.target.value)}>
                    <option value="">All</option>
                    {categories.map(category => (
                        <option key={category} value={category}>{category}</option>
                    ))};
                </select>
            </div>  
        </>
    );
}; export default Filter;
