import { useState, useEffect } from "react";

//Components
import Filters from "./Filters";
/**
 * artist
 * album
 * genre
 * release_date
 * label
 * price
 * tracklist
 * cover_image
 */

const InventoryList = () => {
  const [data, setData] = useState([]);
  const [filteredData, setFilteredData] = useState([]);

  useEffect(() => {
    // Fetch and parse JSON data when the component mounts
    fetch("/data/mock_vinyls.json") // Note: This path is relative to the public directory in CodeSandbox
      .then((response) => response.json())
      .then((jsonData) => {
        console.log(jsonData);
        setData(jsonData);
      })
      .catch((error) => {
        console.error("Error fetching data:", error);
      });
  }, []); //empty dependency array ensures that this effect runs once when the component mounts.

  // Define filter criteria
  const FilterData = () => {
    const genre = data.filter(i => i.genre === 'jazz');
    setFilteredData(genre);
  };
  // call filterData function when componet is renered.
  useEffect(() => {
    FilterData();
  },[data])

  return (
    <div>
      <ul>
        {data.map((item) => (
          <div key={item.id} className="inventory-list">
            <img src={item.cover_image} alt="Album artwork" style={{ width: 300}}/>
            <br />
            <p id="artist">Artist: {item.artist}</p>
            <p id="album">Album: {item.album_title}</p>
            <p id="genre">Genre: {item.genre}</p>
            <p id="releaseDate">Release Date: {item.release_date}</p>
            <p id="label">Label: {item.label}</p>
            <p id="price">Price: {item.price}</p>
            <p id="tracklist">Tracklist: {item.tracklist}</p>
          </div>
        ))}
      </ul>
    </div>
  );
};
export default InventoryList;
