// import { useState, useEffect } from 'react'; 
import InventoryList from "../components/InventoryList";
import Filter from "../components/Filter";

const InventoryPg = () => {
  
  const [records, setRecords] = useState([]);
  const [selectedArtist, setSelectedArtist] = useState('');
  const [selectedGenre, setSelectedGenre] = useState('');
  const [selectedReleaseDate, setSelectedReleaseDate] = useState('');
  const [selectedLabel, setSelectedLabel] = useState('');

  useEffect(() => {
      fetch('/data/mock_vinyls.json')
      .then(res => res.json())
      .then(data => {
          setRecords(data);
          
      })
      .catch(err => console.error(err));
  }, []);
  
  const handleArtistChange = (e) => {
    setSelectedArtist(e.target.value);
  };

  const handleGenreChange = (e) => {
    setSelectedGenre(e.target.value);
  };
  const handleReleaseDateChange = (e) => {
    setSelectedReleaseDate(e.target.value);
  };

  const handleLabelChange = (e) => {
    setSelectedLabel(e.target.value);
  };

  const filterRecords = () => {
    let filteredRecs = records;
    switch(records){
      case selectedArtist:
        filteredRecs = filteredRecs.filter(rec => rec.artist === selectedArtist)
      break;
      case selectedGenre:
        filteredRecs = filteredRecs.filter(rec => rec.genre === selectedGenre)
        break;
      case selectedLabel:
        filteredRecs = filteredRecs.filter(rec => rec.label === selectedLabel)
        break;
      case selectedReleaseDate:
        filteredRecs = filteredRecs.filter(rec => rec.release_date)
    }
    setRecords(filteredRecs);
  }
  
  
  // VIEW
  return (
    <div className="inventory-pg">
      <h1>Inventory Page</h1>
      <Filter
        selectedArtist={selectedArtist}
        selectedGenre={selectedGenre}
        selectedLabel={selectedLabel}
        selectedReleaseDate={selectedReleaseDate}
        filterRecords={filter}
      />
      
      <InventoryList records={records} />
    </div>
  );
};
export default InventoryPg;
