import { useState, useEffect } from 'react'; 

const Filter = ({
    selectedArtist,
    selectedGenre,
    selectedReleaseDate,
    selectedLabel,

    handleSelectedArtist,
    handleSelectedGenre,
    handleSelectedReleaseDate,
    handleSelectedLabel,

    filteredRecs
}) => {

    const [records, setRecords] = useState([])
    useEffect(() => {
        fetch('/data/mock_vinyls.json')
        .then(res => res.json())
        .then(data => {
            setRecords(data);
            setCategories([...new Set(data.map(d => d.artist))])
        })
        .catch(err => console.error(err));
    }, []);

    return (
        <div>
          <label>Artist:</label>
          <select value={selectedArtist} onChange={handleArtistChange}>
            <option value="">All Artists</option>
            {/* Add options dynamically based on your data */}

          </select>
    
          <label>Genre:</label>
          <select value={selectedGenre} onChange={handleGenreChange}>
            <option value="">All Genres</option>
            {/* Add options dynamically based on your data */}
          </select>
    
          <button onClick={filterSongs}>Apply Filters</button>
        </div>
      );
}; export default Filter;
