import { useState, useEffect } from 'react'; 
import InventoryList from "../components/InventoryList";
import Filter from "../components/Filter";

const InventoryPg = () => {
  // Setup State Hooks
  const [products, setProducts] = useState([]);
  const [filteredProducts, setFilteredProducts] = useState([]);
  const [categories, setCategories] = useState([]);

  // Read JSON data
  useEffect(() => {
    fetch('/data/mock_vinyls.json')
    .then(res => res.json())
    .then(data => {
      setProducts(data);
      setFilteredProducts(data);
      setProducts(data);
      setCategories([...new Set(data.map(p => p.artist))])
    })
    .catch(err => console.error(err));
  }, []);
  // handle change in <select> element.
  // const HandleCategoryChange = (selectedCategory) => {
  //   if (selectedCategory === '') {
  //     setFilteredProducts(products); // list all
  //   } else {
  //     const filtered = products.filter(p => p.artist === selectedCategory);
  //     setFilteredProducts(filtered); // list selected filter.
  //   }
  // };
  const HandleArtistChange = (selectedCategory) => {
    if (selectedCategory === '') {
      setFilteredProducts(products); // list all
    } else {
      const filtered = products.filter(p => p.artist === selectedCategory);
      setFilteredProducts(filtered); // list selected filter.
    }
  };
  const HandleGenreChange = (selectedCategory) => {
    if (selectedCategory === '') {
      setFilteredProducts(products); // list all
    } else {
      const filtered = products.filter(p => p.genre === selectedCategory);
      setFilteredProducts(filtered); // list selected filter.
    }
  };
  const HandleAlbumChange = (selectedCategory) => {
    if (selectedCategory === '') {
      setFilteredProducts(products); // list all
    } else {
      const filtered = products.filter(p => p.album === selectedCategory);
      setFilteredProducts(filtered); // list selected filter.
    }
  };
  const HandleRDateChange = (selectedCategory) => {
    if (selectedCategory === '') {
      setFilteredProducts(products); // list all
    } else {
      const filtered = products.filter(p => p.release_date === selectedCategory);
      setFilteredProducts(filtered); // list selected filter.
    }
  };
  const HandleLabelChange = (selectedCategory) => {
    if (selectedCategory === '') {
      setFilteredProducts(products); // list all
    } else {
      const filtered = products.filter(p => p.label === selectedCategory);
      setFilteredProducts(filtered); // list selected filter.
    }
  };
  // VIEW
  return (
    <div className="inventory-pg">
      <h1>Inventory Page</h1>
      <Filter categories={categories} onSelectArtist={HandleArtistChange}/>
      <Filter categories={categories} onSelectAlbum={HandleAlbumChange}/>
      <InventoryList products={filteredProducts}/>
    </div>
  );
};
export default InventoryPg;
