import { useState, useEffect } from 'react'; 

const Filter = ({
    selectedArtist,
    selectedGenre,
    selectedReleaseDate,
    selectedLabel,

    handleArtistChange,
    handleGenreChange,
    handleReleaseDateChange,
    handleLabelChange,

    filterRecords
}) => {
    // data for select list
    const [records, setRecords] = useState([])
    useEffect( () => {
        fetch('/data/mock_vinyls.json')
        .then(res => res.json())
        .then(data => {
            setRecords(data);
            
        })
        .catch(err => console.error(err));
    }, []);

    const handleFilterClick = () => {
        filterRecords();
    }

    return (
        <div>
          <label>Artist:</label>
          <select value={selectedArtist} onChange={handleArtistChange}>
            <option value="">All</option>
            {records.map(rec => (
                <option key={rec.artist}>{rec.artist}</option>
            ))}

          </select>
    
          <label>Genre:</label>
          <select value={selectedGenre} onChange={handleGenreChange}>
            <option value="">All</option>
            {records.map(rec => (
                <option key={rec.genre}>{rec.genre}</option>
            ))}
          </select>
          <label>Label:</label>
          <select value={selectedLabel} onChange={handleLabelChange}>
            <option value="">All</option>
            {records.map(rec => (
                <option key={rec.label}>{rec.label}</option>
            ))}
          </select>
          <label>Release Date:</label>
          <select value={selectedReleaseDate} onChange={handleReleaseDateChange}>
            <option value="">All</option>
            {records.map(rec => (
                <option key={rec.release_date}>{rec.release_date}</option>
            ))}
          </select>
    
          <button onClick={handleFilterClick}>Apply Filters</button>
        </div>
      );
}; export default Filter;
