import { useState, useEffect } from 'react'; 

const Filter = () => {
    // Setup State Hooks
    const [records, setRecords] = useState([])
    const [selectedArtist, setSelectedArtist] = useState('');
    const [selectedGenre, setSelectedGenre] = useState(''); 
    const data = records

    // Read JSON data
    useEffect(() => {
        fetch('/data/mock_vinyls.json')
        .then(res => res.json())
        .then(data => {
            setRecords(data);
            setCategories([...new Set(data.map(p => p.artist))])
        })
        .catch(err => console.error(err));
    }, []);

    const handleArtistChange = (e) => setSelectedArtist(e.target.value);

    const handleGenreChange = (e) => setSelectedGenre(e.target.value);
  
    const filterRecords = () => {
        let filteredRecs = records;

        switch(records){
            case selectedArtist:
                filteredRecs = filteredRecs.filter(r => r.artist === selectedArtist);
            break;
            case selectedGenre:
                filteredRecs = filteredRecs.filter(r => r.genre === selectedGenre);
            break;
        };
        setRecords(filteredRecs);
    };

    return (
        <div>
            <h1>Filters</h1>
            <div>
                <label>Artist</label>
                <select value={selectedArtist} onChange={handleArtistChange}>
                    <option value="">All</option>
                    {records.map(recs => (
                        <option key={recs} value={recs}>{recs}</option>
                    ))}
                </select>
                <label>Artist</label>
                <select value={selectedGenre} onChange={handleGenreChange}>
                    <option value="">All</option>
                    {records.map(recs => (
                        <option key={recs} value={recs}>{recs}</option>
                    ))}
                </select>

                <button onClick={filterRecords}>Update</button>
            </div>
        </div>
    )
    
}; export default Filter;


