    selectedGenre,
    selectedReleaseDate,
    selectedLabel,

    handleArtistChange,
    handleGenreChange,
    handleReleaseDateChange,
    handleLabelChange,

    filteredRecs
}) => {
    // data for select list
    const [records, setRecords] = useState([])
    useEffect(() => {
        fetch('/data/mock_vinyls.json')
        .then(res => res.json())
        .then(data => {
            setRecords(data);
            
        })
        .catch(err => console.error(err));
    }, []);

    

    return (
        <div>
          <label>Artist:</label>
          <select value={selectedArtist} onChange={handleArtistChange}>
            <option value="">All Artists</option>
            {/* Add options dynamically based on your data */}

          </select>
    
